  .TimelineItem-badge {
    border: transparent;
  }

  .dropdown-item:focus,
  .dropdown-item:hover,
  .jump-to-suggestions-results-container [aria-selected="true"] .jump-to-suggestions-path,
  .jump-to-suggestions-results-container .navigation-focus .jump-to-suggestions-path,
  .subnav-item.selected,
  .subnav-item[aria-selected="true"],
  .subnav-item[aria-current]:not([aria-current="false"]),
  .filter-item.selected,
  .filter-item[aria-selected="true"],
  .filter-item[aria-current]:not([aria-current="false"]) {
    background-color: #bd93f91a;
    color: #bd93f9;
  }

  .jump-to-suggestions-results-container [aria-selected="true"] mark,
  .jump-to-suggestions-results-container .navigation-focus mark {
    color: #bd93f9;
  }

  /* Fix the border of the color when active */
  .btn-primary:active {
    border-color: var(--color-btn-primary-border);
  }

  /* Custom Code Font */
  .blob-code-inner {
    if custom-font-enabled {
      font-family: custom-font;
    }
  }

  /* Color the logo */
  .octicon-mark-github {
    if purple-logo {
      color: #bd93f9;
    }
  }

  /* Fix Color on SideNav's for consistency */
  .SideNav {
    background-color: rgba(0, 0, 0, 0);
  }

  /* Fix the background behind some buttons */
  .color-bg-subtle.ml-1 {
    background-color: transparent !important;
  }

  /* Improve labels when colored status is enabled */
  if colored-status {
    .State--open {
      color: #50fa7b;
    }
    .State--merged {
      color: #bd93f9;
    }
    .State--closed {
      color: #ff5555;
    }

    .Label--info,
    .Label--accent {
      background-color: #58a6ff09;
    }
    .Label--success {
      background-color: #50fa7b09;
    }

    .Label--warning,
    .Label--attention {
      background-color: #f1fa8c09;
    }
    .Label--severe {
      background-color: #db6d2809;
    }
    .Label--danger {
      background-color: #f8514909;
    }
    .Label--done {
      background-color: #a371f709;
    }
    .Label--sponsors {
      background-color: #db61a209;
    }
  }
}

/* ==UserStyle==
@name           Oficial Dracula Theme
@namespace      github.com/dracula/youtube
@version        1.0.0
@description    A dark theme for Youtube
@supportURL     https://github.com/dracula/gitlab/issues
@license        MIT
==/UserStyle== */

@-moz-document domain("youtube.com") {
html {
    --background-primary: #282a36;
    --background-secondary: #21222C;
    --background-tertiary: #191A21;
    --current-line: #44475a;
    --selection: #44475a;
    --foreground: #f8f8f2;
    --comment: #6272a4;
    --cyan: #8be9fd;
    --green: #50fa7b;
    --orange: #ffb86c;
    --pink: #ff79c6;
    --purple: #bd93f9;
    --red: #ff5555;
    --yellow: #f1fa8c;
}

html:not(.style-scope)[dark], :not(.style-scope)[dark]
{
  --yt-spec-brand-background-primary: var(--background-secondary);
  --yt-spec-brand-background-secondary: var(--background-tertiary);
  --yt-spec-brand-background-solid: var(--background-primary);
  --yt-spec-general-background-a: var(--background-tertiary);
  --yt-spec-general-background-b: var(--background-secondary);
  --yt-spec-general-background-c: var(--background-primary);
  --yt-spec-error-background: #f9f9f9;
  --yt-spec-text-primary: var(--foreground);
  --yt-spec-text-primary-inverse: var(--background-primary);
  --yt-spec-text-secondary: var(--comment);
  --yt-spec-text-disabled: var(--selection);
  --yt-spec-call-to-action: var(--purple);
  --yt-spec-icon-active-other: var(--purple);
  --yt-spec-icon-inactive: var(--comment);
  --yt-spec-icon-disabled: var(--selection);
  --yt-spec-badge-chip-background: rgba(255, 255, 255, 0.1);
  --yt-spec-verified-badge-background: rgba(255, 255, 255, 0.25);
  --yt-spec-suggested-action: #252a3a;
  --yt-spec-button-chip-background-hover: rgba(255, 255, 255, 0.2);
  --yt-spec-touch-response: #fff;
  --yt-spec-paper-tab-ink: rgba(255, 255, 255, 0.3);
  --yt-spec-filled-button-text: #030303;
  --yt-spec-call-to-action-inverse: #065fd4;
  --yt-spec-brand-icon-active: #fff;
  --yt-spec-brand-icon-inactive: #909090;
  --yt-spec-brand-button-background: #ff5555;
  --yt-spec-brand-link-text: #ff5555;
  --yt-spec-filled-button-focus-outline: rgba(255, 255, 255, 0.7);
  --yt-spec-call-to-action-button-focus-outline: rgba(62, 166, 255, 0.3);
  --yt-spec-brand-text-button-focus-outline: rgba(255, 78, 69, 0.3);
  --yt-spec-inactive-text-button-focus-outline: #606060;
  --yt-spec-ad-indicator: #00aaa7;
  --yt-spec-brand-subscribe-button-background: var(--yt-spec-brand-button-background);
  --yt-spec-wordmark-text: #fff;
  --yt-spec-10-percent-layer: rgba(255, 255, 255, 0.1);
  --yt-spec-selected-nav-text: #fff;
  --yt-spec-themed-blue: #673AB7;
  --yt-spec-themed-green: #50fa7b;
  --yt-spec-themed-overlay-background: rgba(0, 0, 0, 0.8);
}

tp-yt0app-drawer {
    background: var(--background-primary)
}

ytd-masthead
{
  --paper-input-container-input-align: baseline;
  display: block;
  width: 100%;
  background: var(--yt-spec-brand-background-primary);
}

paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button
{
  opacity: 1;
  background-color: #5f6282;
}

.guide-entry-badge.ytd-guide-entry-renderer
{
  color: var(--yt-spec-brand-link-text);
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer
{
  background-color: var(--yt-spec-themed-blue);
}

#notification-count.ytd-notification-topbar-button-renderer
{
  background-color: var(--yt-spec-themed-blue);
}

.sbdd_b, .sbsb_a, .sbfl_b, .sbsb_c
{
  background: var(--yt-spec-brand-background-primary);
}

.gsfs, .sbfl_b
{
  color: #fff;
}

.sbsb_d
{
  background-color: var(--yt-spec-general-background-c);
}

.sbdd_b
{
  border: 1px solid transparent;
}

.sbpqs_a {
    color: #bd93f9;
}

.sbsb_i {
    color: #8be9fd;
}

.sbfl_b {
     color: #8be9fd;   
}
}

/* ==UserStyle==
@name           Oficial Dracula Theme
@namespace      github.com/dracula/hacker-news
@version        1.0.0
@description    A dark theme for Hacker News
@supportURL     https://github.com/dracula/hacker-news/issues
@license        MIT
==/UserStyle== */
@-moz-document url-prefix("https://news.ycombinator.com") {
    #hnmain {
        background-color: #282a36;
    }
    #hnmain > tbody > tr:nth-child(1) > td {
        background-color: #44475a;
    }
    #hnmain > tbody > tr:nth-child(4) > td > table > tbody > tr > td {
        background-color: #6272a4;
    }
    a:link{
        color: #bd93f9;
    }
    .hnmore a:link, a:visited {
        color: #6272a4;
    }
    #hnmain > tbody > tr:nth-child(1) > td > table > tbody > tr > td:nth-child(2) > span > b > a {
        color: #50fa7b;
    }
    .comhead a:link, .subtext a:visited {
        color: #8be9fd;
    }
    .score {
        color: #ffb86c;
    }
    #karma {
        color: #ffb86c;
    }
    #hnmain > tbody > tr:nth-child(1) > td > table > tbody > tr > td:nth-child(3) > span {
        color: #ffb86c;
    }
    #hnmain > tbody > tr:nth-child(4) > td > center:nth-child(6) > form > input[type=text] {
        background-color: #44475a;
    }
    #hnmain > tbody > tr:nth-child(4) > td > center:nth-child(6) > form {
        color: #50fa7b;
    }
    .pagetop a:visited {
        color: #bd93f9;
    }
    .title {
        color: #6272a4;
    }
    body {
        background-color: #282a36;
    }
    .commtext {
        color: #f8f8f2
    }
    textarea, input {
        background-color: #282a36;
        color: #f8f8f2;
        border-color: #8be9fd
    }
}

/* ==UserStyle==
@name           Dracula for Lichess
@namespace      dracula/lichess
@version        0.1.0
@description    Dark mode for lichess.org
@license        MIT License
@author         Jonathan Scholbach
@updateURL      https://github.com/dracula/lichess/raw/master/dracula_for_lichess.user.css
@preprocessor   stylus
==/UserStyle== */
@-moz-document domain("lichess.org") {
  :root {
    /* Taken  from https://draculatheme.com/contribute */
    --dracula-bg: #282a36;
    --dracula-current: #44475a;
    --dracula-comment: #6272a4;
    --dracula-fg: #f8f8f2;
    --dracula-purple: #bd93f9;
    --dracula-pink: #ff79c6;
    --dracula-green: #50fa7b;
    --dracula-orange: #ffb86c;
    --dracula-yellow: #f1fa8c;

    /* Semantic Aliases */
    --color-dark: var(--dracula-bg);
    --color-dim: var(--dracula-current);
    --color-bright: var(--dracula-comment);
    --color-white: var(--dracula-fg);
    --color-primary: var(--dracula-purple);
    --color-primary-transparent: #bd93f9aa;
    --color-highlight: var(--dracula-orange);
    --color-alert: var(--dracula-pink);
    --color-ok: var(--dracula-green);

    --background: var(--color-dark);
    --color: var(--color-white);
  }

  /************************
  *    GLOBAL ELEMENTS    *
  /***********************/
  html,
  body,
  main,
  #main-wrap,
  header,
  div,
  fill,
  input,
  select,
  select option,
  aside,
  dialog,
  button,
  span {
    background: var(--background) !important;
    color: var(--color) !important;
  }

  a,
  .button-link,
  a.button.button-empty {
    color: var(--color-primary);
  }

  a.button.button-empty,
  a.button.button-empty:hover {
    background: var(--background) !important;
    box-shadow: none !important;
    border: none;
  }

  a:hover,
  a.button.button-empty:hover,
  select option:hover {
    color: var(--color-highlight) !important;
  }

  input:focus,
  textarea:focus {
    border-color: var(--color-primary) !important;
  }

  button.disabled,
  button.disabled *,
  button.disabled:before {
    background: var(--color-dim) !important;
  }

  .btn-rack__btn.active,
  .button.active,
  .data-count::after,
  .unread {
    background-color: var(--color-alert) !important;
    color: var(--color-dark) !important;
    text-shadow: none !important;
  }

  .cmn-toggle:checked + label {
    background: var(--color-ok);
  }

  .cmn-toggle--subtle:not(:checked) + label {
    background: var(--color-bright);
  }

  @keyframes spinner-color {
    0%,
    100% {
      stroke: var(--color-primary);
    }
    25% {
      stroke: var(--color-alert);
    }
    50% {
      stroke: var(--dracula-yellow);
    }
    75% {
      stroke: var(--color-ok);
    }
  }

  .white .spinner path {
    stroke: var(--color-primary);
  }

  /**
  /* NAVIGATION */
  /**/
  header {
    /* z-index is necessary here, because of the z-index on .time */
    z-index: 2;
  }

  header a {
    color: var(--color-bright) !important;
  }

  h1.site-title a {
    color: var(--color-primary) !important;
  }

  h1.site-title a:hover {
    color: var(--color-highlight) !important;
  }

  h1.site-title a span {
    color: var(--color-dim) !important;
  }

  div[role="group"],
  #topnav section a:hover,
  #topnav section:has(div[role="group"]:hover) a {
    border-left-color: var(--color-primary) !important;
  }

  #topnav section:has(div[role="group"]:hover) > a,
  #topnav section:has(div[role="group"]:hover),
  #topnav section a:hover,
  #topnav section a:hover span {
    background: var(--color-dim) !important;
    color: var(--color-primary) !important;
  }

  div[role="group"] a:hover {
    background: var(--color-primary) !important;
    color: var(--color-dark) !important;
  }

  .site_notification {
    z-index: 1;
  }

  .site_notification.new,
  .site_notification.new * {
    background: var(--color-dim) !important;
  }

  .site_notification:hover,
  .site_notification:hover * {
    background: var(--color-bright) !important;
  }

  i:before,
  .utitle {
    color: var(--color-primary) !important;
  }

  bad {
    color: var(--color-alert) !important;
  }

  good {
    color: var(--color-ok) !important;
  }

  div#friend_box {
    border: 2px solid var(--color-dim);
  }

  .friend_box_title {
    background: var(--background) !important;
    color: var(--color) !important;
  }

  .game__meta,
  .mchat,
  .mchat__content discussion,
  .round__app__table {
    background: var(--background);
    border: 1px solid var(--color-dim);
  }

  /************************
  *      LOBBY VIEW       *
  /***********************/
  .lobby__start div,
  .lobby__start button,
  .lobby__spotlights a,
  .lobby__spotlights a * {
    background: var(--color-dim) !important;
    color: var(--color) !important;
  }

  .ublog-post-card {
    background: var(--background) !important;
    color: var(--color) !important;
  }

  .lobby__timeline div:hover a {
    background: var(--background) !important;
    color: var(--color-bright) !important;
  }

  .lobby__box,
  .lobby__spotlights,
  .ublog-post-card,
  .lobby__feed,
  .lobby__support a,
  .lobby__app__content {
    box-shadow: none !important;
  }

  .lobby__start button:hover,
  .lobby__support a:hover *,
  .lobby__support a:hover .lobby__support__text,
  .lobby__spotlights a:hover,
  .lobby__spotlights a:hover * {
    background: var(--color-primary) !important;
    color: var(--color-dark) !important;
  }

  .lobby__support a,
  .lobby__support a * {
    background: var(--color-dim) !important;
  }

  .lobby__support a:hover,
  .lobby__support a:hover * {
    background: var(--color-primary) !important;
  }

  .lobby__support a:hover i:before,
  .lobby__spotlights a:hover i:before {
    color: var(--background) !important;
  }

  .lobby__timeline div a:hover {
    color: var(--color-primary) !important;
  }

  div.lobby__app div.tabs-horiz span.active,
  div.lobby__app div.tabs-horiz span:hover,
  .analyse__underboard__menu > span.active {
    border-bottom-color: var(--color-primary) !important;
  }

  div.lobby__app__content div {
    border-color: var(--color-dim) !important;
  }

  div.lobby__app__content div:hover,
  div.lobby__app__content div:hover div {
    background: var(--color-dim) !important;
  }

  .lobby__streams .stream.highlight strong {
    color: var(--color-primary) !important;
  }

  main.lobby .lobby__tournaments table tr:nth-child(even) *,
  main.lobby .lobby__box__content table tr:nth-child(even) * {
    background: var(--color-dim) !important;
  }

  .daily-feed__updates {
    border-left-color: var(--color-primary) !important;
  }

  .daily-feed__update > i {
    border: 3px solid var(--color-primary) !important;
    background-color: var(--color-dim) !important;
  }

  .daily-feed__update__day {
    color: var(--color-primary) !important;
  }

  /************************
  *       ROUND VIEW       *
  /***********************/
  #zenzone a#zentog,
  #zenzone a#zentog:before {
    background: var(--color-dim) !important;
  }

  .mchat__tab.mchat__tab-active,
  .mchat__tab.mchat__tab-active * {
    background: var(--color-dim) !important;
  }

  .mchat__tab input:checked {
    border-color: var(--color-ok) !important;
  }

  .fbt:hover,
  .fbt:hover *,
  .fbt:hover:before {
    background: var(--color-highlight) !important;
    color: var(--color-dark) !important;
  }

  .fbt,
  .fbt:before {
    background: var(--background);
    color: var(--color);
  }

  .rcontrols .rematch-decline,
  rm6 .buttons,
  .rclock .time,
  .round__app__table,
  .context-streamer,
  .game__meta,
  .mchat,
  .crosstable {
    box-shadow: none !important;
  }

  div.crosstable__users {
    background: var(--background);
    border: 1px solid var(--color-dim);
  }

  div.crosstable .current a {
    background: var(--color-primary);
    color: var(--color-dark);
  }

  .crosstable .win,
  .crosstable .loss {
    color: var(--color-dark) !important;
  }

  .crosstable .win {
    background: var(--color-ok) !important;
  }

  .crosstable .loss {
    background: var(--color-alert) !important;
  }

  move:hover,
  move:hover san {
    background: var(--color-primary) !important;
    color: var(--color-dark) !important;
  }

  .rclock-top .time,
  .time {
    background-color: var(--color-primary) !important;
    color: var(--color-dark) !important;
    z-index: 1;
  }

  index,
  move,
  rm6,
  l4x,
  l4x,
  i5z {
    background: var(--background) !important;
    color: var(--color) !important;
  }

  kwdb.alt {
    background-color: var(--color-dim) !important;
  }

  kwdb:hover {
    background: var(--color-primary-transparent) !important;
    color: var(--color-dark) !important;
  }

  /************************
  *     ANALYSIS VIEW
  /***********************/
  div.analyse__underboard__menu span:hover {
    background: var(--color-dim) !important;
  }

  *[stroke="#003088"] {
    stroke: var(--dracula-pink);
    opacity: 0.7;
  }

  *[fill="#3291ff"],
  #arrowhead-pb path {
    fill: var(--dracula-pink) !important;
  }

  *[stroke="#15781B"] {
    stroke: var(--dracula-green) !important;
    opacity: 0.7;
  }

  /************************
  *         BOARD         *
  /***********************/
  cg-board {
    background-image: url("https://raw.githubusercontent.com/dracula/lichess/master/images/board.svg") !important;
  }

  square.check {
    background: radial-gradient(
      ellipse at center,

      var(--color-alert) 0%,

      var(--color-alert) 25%,

      rgba(169, 0, 0, 0) 89%,

      rgba(158, 0, 0, 0) 100%
    );
  }

  square.last-move {
    background: var(--color-primary);
    opacity: 40%;
  }

  square.current-premove {
    background: var(--color-highlight) !important;
    opacity: 60%;
  }

  square.selected {
    background: var(--color-ok) !important;
    opacity: 60%;
  }

  /************************
  *      PUZZLE VIEW      *
  /***********************/
  .tview2 move.current {
    border-color: var(--color-primary);
  }

  .puzzle__tools,
  .puzzle__side__metas,
  .puzzle__side__theme,
  .puzzle__side__replay,
  .puzzle__side__config,
  .puzzle__side__user,
  group.radio {
    box-shadow: none;
    border: 2px solid var(--color-dim);
  }

  .puzzle__side__metas div:before {
    color: var(--color-primary) !important;
  }

  .puzzle__moves move.fail glyph {
    color: var(--color-alert);
  }

  .puzzle__feedback {
    background: var(--background) !important;
    border-style: solid;
    border-width: 2px;
  }

  .puzzle__feedback.play {
    border-color: var(--color-primary) !important;
  }

  .puzzle__feedback.good,
  .puzzle__feedback.after {
    border-color: var(--color-ok) !important;
  }

  .puzzle__vote .vote.vote-down {
    color: var(--color-alert) !important;
  }

  .puzzle__vote .vote.vote-up {
    color: var(--color-ok) !important;
  }

  .puzzle__feedback.good .icon {
    color: var(--color-ok) !important;
  }

  .puzzle__feedback.fail {
    border-color: var(--color-alert) !important;
  }

  .puzzle__feedback.fail .icon {
    color: var(--color-alert) !important;
  }

  .puzzle__session .result-false {
    background: var(--color-alert) !important;
    color: var(--color-dark) !important;
  }

  .puzzle__session .result-true {
    background: var(--color-ok) !important;
    color: var(--color-dark) !important;
  }

  .puzzle__session .result-cursor.current {
    background: var(--color-dim) !important;
  }

  /************************
  *      LEARN VIEW      *
  /***********************/
  .learn-stages .stage.future,
  .learn-stages .stage.future * {
    background: var(--color-alert);
    color: var(--color-dark);
  }

  .learn-stages .stage.ongoing,
  .learn-stages .stage.ongoing *,
  .learn__screen .buttons .next,
  .learn__table .progress a.active,
  .learn__table .progress a.active *,
  .learn__side-map .categ.active > h2,
  .learn__side-map .stage.active,
  .learn__side-map .stage.active *,
  .learn__side-map .stage.active img,
  .learn__side-map .stage.future img {
    background: var(--color-bright) !important;
    color: var(--color-dark) !important;
  }

  .learn__table .progress a.done,
  .learn__table .progress a.done *,
  .learn__side-map .stage.done img {
    background: var(--color-ok) !important;
    color: var(--color-dark) !important;
  }

  .learn__side-map a.stage:hover,
  .learn__side-map a.stage:hover * {
    background: var(--color-primary) !important;
    color: var(--color-dark) !important;
  }

  .learn__table .progress a.done i:before {
    color: var(--color-dark) !important;
  }

  .learn__screen .buttons .back {
    background: var(--background);
    border: 1px solid var(--color-dim);
  }

  div.learn__table {
    border: 2px solid var(--color-primary);
  }

  /************************
  *      WATCH VIEW      *
  /***********************/
  .tv-channel.best > span::before {
    color: var(--color-highlight);
  }

  .tv-channel > span:before {
    color: var(--color-primary);
  }

  /************************
  *    COMMUNITY VIEW     *
  /***********************/
  .community__leaders .leaderboards h2,
  .community__leaders .leaderboards h2 a {
    background: var(--color-bright) !important;
    color: var(--color-dark) !important;
  }

  .community__leaders .leaderboards .user-top {
    border-color: var(--color-dim) !important;
  }

  .community__leaders .leaderboards .user-top ol li:nth-child(even) *,
  .community__leaders .leaderboards .user-top ol li:nth-child(even) {
    background: var(--color-dim) !important;
  }
}

/* ==UserStyle==
@name         Dracula for GitLab
@description  A dark theme for GitLab
@namespace    github.com/dracula/gitlab
@version      1.0.0
@homepageURL  https://draculatheme.com/gitlab
@supportURL   https://github.com/dracula/gitlab/issues
@license      MIT
