@namespace    USO Archive
@author       alexchaichan
@description  A Dracula theme with customize-able page width for <a href="https://docs.python.org">python documentation</a>.
@version      20210728.19.16
@license      CC0-1.0
@preprocessor uso
@advanced text max-page-width "Maximum Page Width" "1090px"
@advanced text font-size "Base Font Size" "16px"
==/UserStyle== */
@-moz-document domain("docs.python.org") {
    body {
        font: /*[[font-size]]*/
        /1.4 "Open Sans", Georgia, Times, "Times New Roman", serif;
        background-color: #282a36;
        color: #f8f8f2;
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        font-family: "Fira Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    }
    table,
    td,
    tr,
    th {
        background-color: inherit;
    }
    input,
    textarea,
    select,
    button {
        background-color: #44475a;
        color: #f8f8f2;
        border: 1px solid #6272a4;
    }
    div.body ul > li {
        margin-top: 1em;
    }
    div.body ul > li > ul > li {
        margin-top: 0.5em;
    }
    div.related {
        background-color: #44475a;
        color: #f8f8f2;
        border-top: 1px solid #6272a4;
    }
    div.related a {
        color: #bd93f9;
    }
    div.related a:hover {
        color: #ff79c6;
    }
    div.related:first-child {
        border: 1px solid #6272a4;
    }
    .inline-search {
        display: inline;
    }
    form.inline-search input {
        display: inline;
    }
    form.inline-search input[type="submit"] {
        width: 30px;
    }
    div.sphinxsidebar {
        background-color: #44475a;
        background-color: #282a36;
        border-radius: 0;
        line-height: 130%;
        font-size: smaller;
        font-family: sans-serif;
    }
    div.sphinxsidebar h3,
    div.sphinxsidebar h4 {
        border-bottom-color: #6272a4;
        font-weight: 500;
        color: #50fa7b;
        margin-top: 1.5em;
    }
    div.sphinxsidebar h3 a {
        color: #ffb86c;
    }
    div.sphinxsidebarwrapper > h3:first-child {
        margin-top: 0.2em;
    }
    div.sphinxsidebarwrapper > ul > li > ul > li {
        margin-bottom: 0.4em;
    }
    div.sphinxsidebar a {
        color: #8be9fd;
    }
    div.sphinxsidebar a:hover {
        color: #ff79c6;
    }
    div#sidebarbutton {
        background-color: inherit !important;
        border-radius: 0 !important;
    }
    div#sidebarbutton:hover {
        background-color: #282a36 !important;
    }
    form.inline-search input,
    div.sphinxsidebar input {
        font-family: 'Lucida Grande', Arial, sans-serif;
        border: 1px solid #6272a4;
        font-size: smaller;
        border-radius: 3px;
        padding: 5px 8px;
        background-color: #44475a;
        color: #f8f8f2;
        box-shadow: 0 0 0 1px #50fa7b, 0 0 0 2px transparent;
    }
    div.sphinxsidebar input[type=text] {
        max-width: 150px;
    }
    div.document {
        background-color: inherit;
        max-width: /*[[max-page-width]]*/;
        margin: 0 auto;
        border: 1px solid #282a36;
    }
    div.body {
        max-width: 100%;
        padding: 10px 20px;
        color: inherit;
        background-color: #282a36;
        border-left: 1px solid #282a36;
    }
    div.body h1,
    div.body h2,
    div.body h3,
    div.body h4,
    div.body h5,
    div.body h6 {
        color: #bd93f9;
        background-color: inherit;
        font-weight: bold;
        border-bottom: 1px solid #6272a4;
    }
    div.body h2,
    div.body h3,
    div.body h4,
    div.body h5,
    div.body h6 {
        margin-top: 2em;
    }
    div.body p {
        line-height: 1.45em;
        text-align: left;
    }
    div.body dd p {
        margin: 1em 0 1em 2em;
    }
    div.body hr {
        border: 0;
        background-color: #6272a4;
        height: 1px;
    }
    div.body pre {
        border: 0px solid #6272a4;
        background-color: #44475a;
        color: #f8f8f2;
        padding: 15px;
        font-family: "Fira Code", "Source Code Pro", Menlo, Monaco, Consolas, "DejaVu Sans Mono", Inconsolata, monospace;
        font-size: 1em;
        line-height: 1.5;
    }
    span.copybutton {
        padding: 3px 4px 3px 3px;
        border-radius: 2px !important;
        font-size: 80%;
        top: 3px;
        right: 3px;
        color: #f8f8f2 !important;
        background-color: rgba(255, 121, 198, 0.2) !important;
        border-color: #6272a4 !important;
    }
    div.topic {
        background-color: #44475a;
        border: 1px solid #6272a4;
    }
    div.body div.admonition,
    div.body div.impl-detail {
        text-align: left;
        border-radius: 0;
        margin-left: 2em;
        border-width: 1px 1px 1px 5px;
    }
    div.body div.admonition.note {
        background-color: #6272a4;
        border-color: #44475a;
    }
    div.body div.admonition.warning {
        border-color: #ff5555;
        background-color: #282a36;
    }
    div.body div.admonition.seealso {
        border-color: #50fa7b;
        background-color: #282a36;
    }
    div.body div.admonition > p,
    div.body div.impl-detail > p {
        margin: 0;
    }

    div.body a {
        color: #8be9fd;
    }
    div.body a:visited {
        color: #ff79c6;
    }
    div.body a:hover {
        color: #ff79c6;
    }
    tt,
    code,
    pre {
        font-family: monospace, sans-serif;
        font-size: 96.5%;
    }
    div.body tt,
    div.body code {
        border-radius: 1px;
        font-weight: 500;
        color: #bd93f9;
        background-color: inherit;
    }
    div.body tt.descname,
    div.body code.descname {
        font-size: 120%;
    }
    div.body tt.xref,
    div.body a tt,
    div.body code.xref,
    div.body a code {
        font-weight: normal;
    }
    .versionadded {
        border-radius: 0;
        background: inherit;
        border: none;
        padding: 0;
        border-color: #1b1819;
    }
    .versionadded p {
        font-weight: bold;
    }
    .versionadded .versionmodified {
        font-style: normal;
        color: #50fa7b;
    }
    .versionchanged {
        border-radius: 0;
        background: inherit;
        border: none;
        padding: 0;
        border-color: #1b1819;
    }
    .versionchanged p {
        font-weight: bold;
    }
    .versionchanged .versionmodified {
        font-style: normal;
        color: #ffb86c;
    }
    .deprecated {
        border-radius: 0;
        background: inherit;
        border: none;
        padding: 0;
        border-color: #1b1819;
        color: #ff5555;
    }
    .deprecated p {
        font-weight: bold;
    }
    .deprecated .versionmodified {
        font-style: normal;
    }
    table.docutils {
        border: 1px solid #6272a4;
        min-width: 20%;
        max-width: 90%;
        border-radius: 3px;
        margin: 10px auto;
    }
    table.docutils td,
    table.docutils th {
        border: 1px solid #6272a4 !important;
        border-radius: 3px;
    }
    table p,
    table li {
        text-align: left !important;
    }
    table.docutils th {
        background-color: inherit;
        padding: 0.3em 0.5em;
    }
    table.docutils td {
        background-color: inherit;
        padding: 0.3em 0.5em;
    }
    table.footnote,
    table.footnote td {
        border: 0 !important;
    }
    div.footer {
        line-height: 150%;
        margin-top: -2em;
        text-align: right;
        width: auto;
        margin-right: 10px;
    }
    div.footer a:hover {
        color: #ff79c6;
    }
    .refcount {
        color: #50fa7b;
    }
    .stableabi {
        color: #8be9fd;
    }
    .highlight {
        background: none !important;
    }
    dt:target,
    span.highlighted {
        background-color: #44475a;
        border-color: #6272a4;
    }
    .section > dl {
        margin-top: 4em;
        margin-bottom: 4em;
    }
    .section > dl > dt {
        border: 1px solid #6272a4;
        background-color: #44475a;
        color: #f8f8f2;
        padding: 5px 10px;
        font-size: 1.2em;
        margin-bottom: 2em;
    }
    dl.function > dt,
    dl.function > dd {
        margin-top: 1em;
        margin-bottom: 1em;
    }
    .section > dl > dd {
        padding: 0 1em;
        margin-left: 2em;
        font-size: 0.85em;
    }
    .section > dl > dd > dl > dt {
        margin-top: 1em;
        margin-bottom: 1em;
        border: none;
        background: none;
        font-size: 1em;
    }
    .section > dl > dd > dl > dt:first-of-type {
        margin-top: 0.5em;
    }
}
